import React from 'react';

const ResumePreview = ({ information, sections }) => {
  // Helper function to determine if a section should show its title
  const shouldShowTitle = (sectionKey) => {
    const sectionInfo = information[sections[sectionKey]];
    
    // If the section has been saved at least once (has a sectionTitle)
    // and has some content, show the title
    if (!sectionInfo || !sectionInfo.sectionTitle) return false;
    
    // Check if section has valid content
    if (sectionInfo.detail) {
      // For basic sections with a detail property
      if (typeof sectionInfo.detail === 'string') {
        return sectionInfo.detail.trim() !== '';
      } else if (typeof sectionInfo.detail === 'object') {
        // For sections like basicInfo with object details
        return Object.values(sectionInfo.detail).some(val => val && val.trim() !== '');
      }
    } else if (sectionInfo.details && sectionInfo.details.length > 0) {
      // For sections with multiple details (like work experience)
      return sectionInfo.details.some(detail => 
        Object.values(detail).some(val => 
          val && (typeof val === 'string' ? val.trim() !== '' : true)
        )
      );
    } else if (sectionInfo.points && sectionInfo.points.length > 0) {
      // For sections with points (like achievements)
      return sectionInfo.points.some(point => point && point.trim() !== '');
    }
    
    return false;
  };

  // Render a specific section
  const renderSection = (sectionKey) => {
    const sectionInfo = information[sections[sectionKey]];
    if (!sectionInfo) return null;
    
    return (
      <div className="section" key={sectionKey}>
        {shouldShowTitle(sectionKey) && (
          <div className="section-title">{sectionInfo.sectionTitle}</div>
        )}
        
        {/* Render section content based on type */}
        {renderSectionContent(sectionKey, sectionInfo)}
      </div>
    );
  };
  
  // Render specific content for each section type
  const renderSectionContent = (sectionKey, sectionInfo) => {
    switch (sections[sectionKey]) {
      case sections.basicInfo:
        return renderBasicInfo(sectionInfo);
      case sections.workExp:
        return renderWorkExperience(sectionInfo);
      case sections.project:
        return renderProjects(sectionInfo);
      case sections.education:
        return renderEducation(sectionInfo);
      case sections.achievement:
        return renderAchievements(sectionInfo);
      case sections.summary:
        return renderSummary(sectionInfo);
      case sections.other:
        return renderOther(sectionInfo);
      default:
        return null;
    }
  };
  
  // Render functions for each section type
  const renderBasicInfo = (sectionInfo) => {
    const { detail } = sectionInfo;
    if (!detail) return null;
    
    return (
      <div className="basic-info">
        {detail.name && <h1>{detail.name}</h1>}
        {detail.title && <p className="title">{detail.title}</p>}
        
        <div className="contact-info">
          {detail.email && <div>{detail.email}</div>}
          {detail.phone && <div>{detail.phone}</div>}
          {detail.linkedin && <div>{detail.linkedin}</div>}
          {detail.github && <div>{detail.github}</div>}
        </div>
      </div>
    );
  };
  
  const renderWorkExperience = (sectionInfo) => {
    const { details } = sectionInfo;
    if (!details || details.length === 0) return null;
    
    return (
      <div className="work-experience">
        {details.map((work, index) => (
          <div className="work-item" key={index}>
            {work.title && <h3>{work.title}</h3>}
            {work.companyName && <div className="company">{work.companyName}</div>}
            
            <div className="work-details">
              {(work.startDate || work.endDate) && (
                <span className="date">
                  {work.startDate} - {work.endDate || 'Present'}
                </span>
              )}
              {work.location && <span className="location">{work.location}</span>}
            </div>
            
            {work.points && work.points.length > 0 && (
              <ul className="points">
                {work.points.map((point, i) => point && (
                  <li key={i}>{point}</li>
                ))}
              </ul>
            )}
          </div>
        ))}
      </div>
    );
  };
  
  const renderProjects = (sectionInfo) => {
    const { details } = sectionInfo;
    if (!details || details.length === 0) return null;
    
    return (
      <div className="projects">
        {details.map((project, index) => (
          <div className="project-item" key={index}>
            {project.title && <h3>{project.title}</h3>}
            {project.overview && <p className="overview">{project.overview}</p>}
            
            <div className="project-links">
              {project.link && <a href={project.link} target="_blank" rel="noreferrer">Live Demo</a>}
              {project.github && <a href={project.github} target="_blank" rel="noreferrer">GitHub</a>}
            </div>
            
            {project.points && project.points.length > 0 && (
              <ul className="points">
                {project.points.map((point, i) => point && (
                  <li key={i}>{point}</li>
                ))}
              </ul>
            )}
          </div>
        ))}
      </div>
    );
  };
  
  const renderEducation = (sectionInfo) => {
    const { details } = sectionInfo;
    if (!details || details.length === 0) return null;
    
    return (
      <div className="education">
        {details.map((edu, index) => (
          <div className="education-item" key={index}>
            {edu.title && <h3>{edu.title}</h3>}
            {edu.college && <div className="college">{edu.college}</div>}
            
            {(edu.startDate || edu.endDate) && (
              <div className="date">
                {edu.startDate} - {edu.endDate || 'Present'}
              </div>
            )}
          </div>
        ))}
      </div>
    );
  };
  
  const renderAchievements = (sectionInfo) => {
    const { points } = sectionInfo;
    if (!points || points.length === 0) return null;
    
    return (
      <div className="achievements">
        <ul>
          {points.map((achievement, index) => achievement && (
            <li key={index}>{achievement}</li>
          ))}
        </ul>
      </div>
    );
  };
  
  const renderSummary = (sectionInfo) => {
    const { detail } = sectionInfo;
    if (!detail) return null;
    
    return (
      <div className="summary">
        <p>{detail}</p>
      </div>
    );
  };
  
  const renderOther = (sectionInfo) => {
    const { detail } = sectionInfo;
    if (!detail) return null;
    
    return (
      <div className="other">
        <p>{detail}</p>
      </div>
    );
  };
  
  return (
    <div className="resume-preview">
      {/* Render all sections in order */}
      {Object.keys(sections).map(key => renderSection(key))}
    </div>
  );
};

export default ResumePreview;
